import { NavLink } from 'react-router-dom'
import { Redirect } from 'react-router-dom'
import {Route, Routes, Navigate} from "react-router-dom"
import React, { useState } from "react";
import { Button } from "./styles";
import styled from "styled-components";


import Login from './Login'
import NavBar from './NavBar'
import AboutPage from './AboutPage'
import Homepage from './Homepage'
import LoginForm from "../components/LoginForm";
import SignUpForm from "../components/SignUpForm";


function UnauthenticatedApp({ onLogin, setCurrentMember}){


  const [showLogin, setShowLogin] = useState(true);


    const Logo = styled.h1`
      font-family: "Permanent Marker", cursive;
      font-size: 3rem;
      color: deeppink;
      margin: 8px 0 16px;
    `;

    const Wrapper = styled.section`
      max-width: 500px;
      margin: 40px auto;
      padding: 16px;
    `;

    const Divider = styled.hr`
      border: none;
      border-bottom: 1px solid #ccc;
      margin: 16px 0;
    `;

    console.log(setCurrentMember)
    return(
        <div>
        <NavBar setCurrentMember={setCurrentMember}/>
        <Routes>
            {/* <Route path="/bulletins" element={<BulletinContainer showBulletins={showBulletins} setShowBulletins={setShowBulletins}/>}/> */}
            <Route path="/" element={<Homepage/>}/>
            <Route path="/about" element={<AboutPage/>} />
            <Route path="/login" element={<Login setCurrentMember={setCurrentMember}/>}/>
        </Routes>

        <div>
            <NavBar setCurrentMember={setCurrentMember}/>
            <Wrapper>
                <Logo>Richmond Church</Logo>
                {showLogin ? (
                <>
                    <LoginForm onLogin={onLogin} setCurrentMember={setCurrentMember} />
                    <Divider />
                    <p>
                    Don't have an account? &nbsp;
                    <Button color="secondary" onClick={() => setShowLogin(false)}>
                        Sign Up
                    </Button>
                    </p>
                </>
                ) : (
                <>
                    <SignUpForm onLogin={onLogin} />
                    <Divider />
                    <p>
                    Already have an account? &nbsp;
                    <Button color="secondary" onClick={() => setShowLogin(true)}>
                        Log In
                    </Button>
                    </p>
                </>
                )}
            </Wrapper>
            </div>
        </div>
    )
}
export default UnauthenticatedApp
